import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useMutation } from '@apollo/client';
import { LOGIN_USER } from '../utils/mutations';

import Auth from '../utils/auth';

const loginPageDec = {
  background: {
    backgroundColor: 'grey',
    paddingTop: 15,
    paddingBottom: 600,
    marginTop: 0, 
  },
  
  loginBanner:{
    color:'white',
    fontSize: 25,
    fontFamily: 'Times New Roman', 
    textDecoration: 'none',
    backgroundColor: 'black',
    paddingBottom: 0,
    marginBottom: -10
   },
  submitButton:{
    color:'white',
    fontSize: 20,
    fontFamily: 'Brush Script', 
    textDecoration: 'none',
    backgroundColor: 'maroon',
    paddingBottom: 0,
    marginBottom: 50, 
   },
}

const Login = (props) => {
  const [formState, setFormState] = useState({ email: '', password: '' });
  const [login, { error, data }] = useMutation(LOGIN_USER);

  // update state based on form input changes
  const handleChange = (event) => {
    const { name, value } = event.target;

    setFormState({
      ...formState,
      [name]: value,
    });
  };

  // submit form
  const handleFormSubmit = async (event) => {
    event.preventDefault();
    console.log(formState);
    try {
      const { data } = await login({
        variables: { ...formState },
      });

      Auth.login(data.login.token);
    } catch (e) {
      console.error(e);
    }

    // clear form values
    setFormState({
      email: '',
      password: '',
    });
  };

  return (
  
    
   
    <main className="flex-row justify-center" style={loginPageDec.background}>
      
      <div className="col-12 col-lg-6">
        <div className="card text-center">
          <h4 className="card-header text-center" style={loginPageDec.loginBanner}><b>LOGIN INFO</b></h4>


          <div className="card-body">
            {data ? (
              <p>
                Success! You may now head{' '}
                <Link to="/">back to the homepage.</Link>
              </p>
            ) : (
              <form onSubmit={handleFormSubmit}>
                <input
                  className="form-input"
                  placeholder="Email Address"
                  name="email"
                  type="email"
                  value={formState.email}
                  onChange={handleChange}
                />
                
                <input
                  className="form-input"
                  placeholder="Password"
                  name="password"
                  type="password"
                  value={formState.password}
                  onChange={handleChange}
                />
                
                <button
                  className="btn btn-block btn-primary" 
                  style={loginPageDec.submitButton}
                  type="submit"
                >
                  
                  <b>SUBMIT</b>
                </button>
              </form>
            )}

            {error && (
              <div className="my-3 p-3 bg-danger text-white">
                {error.message}
              </div>
            )}
          </div>
       </div>
       </div>
    </main>

  );
};

export default Login;
